import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-eventos',
  templateUrl: './eventos.component.html',
  styleUrls: ['./eventos.component.scss'],
})
export class EventosComponent implements OnInit {
  public eventos: any = [];
  viewItem: boolean = true;

  constructor(private http: HttpClient) { }

  ngOnInit(): void {
    this.getEventos();
  }

  view() {
    this.viewItem = !this.viewItem;
  }

  public getEventos(): void {
    this.http.get('https://localhost:7168/api/Eventos').subscribe({
      next: (response) => (this.eventos = response),
      error: (error) => console.log(error)
    }
    );
  }
}
<h2>Eventos</h2>
<hr>
<form action="" class="form-inline">
  <div class="form-group ">
    <input type="text" class="form-control " placeholder="buscar">
    <button class="btn btn-outline-success">Buscar</button>
  </div>
</form>
<table class="table table-striped">
  <thead>
    <tr>
      <th>#</th>
      <th (click)="view()" type="button"><button type="button" class="btn btn-outline-dark btn-sm">{{viewItem ?
          'Exibir' : 'Ocultar'}}</button></th>
      <th>Tema</th>
      <th>Local</th>
      <th>Data</th>
      <th>Qtd de Pessoas</th>
      <th>Lote</th>
      <th>Opções</th>
    </tr>
  </thead>
  <tbody *ngIf="eventos && eventos.length">
    <tr *ngFor="let evento of eventos">
      <td>{{evento.eventoId}}</td>
      <td><img [isAnimated]="true" [collapse]="viewItem" src="../../assets/Img/{{evento.imagemURL}}" length="60px"
          width="100px"></td>
      <td>{{evento.tema}}</td>
      <td>{{evento.local}}</td>
      <td>{{evento.dataEvento}}</td>
      <td>{{evento.qtdPessoas}}</td>
      <td>{{evento.lote}}</td>
      <td></td>
    </tr>
  </tbody>
  <tfoot *ngIf="!eventos.length">
    <tr>
      <td colspan="8" class="text-center">
        <h4>Nenhum evento encontrado</h4>
      </td>
    </tr>

  </tfoot>
</table>
